import React, { useState, useEffect } from "react";
import {
  Typography,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Paper,
} from "@material-ui/core";

const NearbyPlacesPage = () => {
  const [location, setLocation] = useState("");
  const [Places, setPlaces] = useState([]);

  // Dummy data for demonstration
  const dummyPlaces = [
    { name: "Place A", rating: 4.5, priceRange: "$$$" },
    { name: "Place B", rating: 4.2, priceRange: "$$" },
    { name: "Place C", rating: 4.8, priceRange: "$$$$" },
    { name: "Place D", rating: 3.9, priceRange: "$" },
    { name: "Place E", rating: 4.7, priceRange: "$$$$" },
  ];

  // Function to fetch nearby Places (replace with your API call)
  const fetchNearbyPlaces = async () => {
    // Simulating API call delay
    setTimeout(() => {
      setPlaces(dummyPlaces);
    });
  };

  useEffect(() => {
    // Fetch nearby Places when the component mounts (commented for dummy data)
    // fetchNearbyPlaces();
  }, [location]);

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const handleSearch = () => {
    // Trigger a new Place search when the user clicks the search button
    fetchNearbyPlaces();
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Nearby Places
      </Typography>
      <TextField
        label="Enter Location"
        variant="outlined"
        fullWidth
        value={location}
        onChange={handleLocationChange}
      />
      <Button variant="contained" color="primary" onClick={handleSearch}>
        Search
      </Button>

      <List>
        {Places.map((Place, index) => (
          <Paper
            key={index}
            elevation={3}
            style={{ margin: "16px", padding: "16px" }}
          >
            <ListItem>
              <ListItemText
                primary={Place.name}
                secondary={`Rating: ${Place.rating} - Price Range: ${Place.priceRange}`}
              />
            </ListItem>
          </Paper>
        ))}
      </List>
    </div>
  );
};

export default NearbyPlacesPage;
