import React, { useState, useEffect } from "react";
import {
  Typography,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Paper,
} from "@mui/material";

const NearbyHotelsPage = () => {
  const [location, setLocation] = useState("");
  const [hotels, setHotels] = useState([]);

  // Function to fetch nearby hotels (you need to implement this)
  const fetchNearbyHotels = async () => {
    // Replace with your API call to fetch hotel data based on the location.
    // Example API call: const response = await fetch(`https://api.example.com/hotels?location=${location}`);
    // const data = await response.json();
    // setHotels(data);
  };

  useEffect(() => {
    // Fetch nearby hotels when the component mounts
    fetchNearbyHotels();
  }, [location]);

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const handleSearch = () => {
    // Trigger a new hotel search when the user clicks the search button
    fetchNearbyHotels();
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Nearby Hotels
      </Typography>
      <TextField
        label="Enter Location"
        variant="outlined"
        fullWidth
        value={location}
        onChange={handleLocationChange}
      />
      <Button variant="contained" color="primary" onClick={handleSearch}>
        Search
      </Button>

      <List>
        {hotels.map((hotel, index) => (
          <Paper
            key={index}
            elevation={3}
            style={{ margin: "16px", padding: "16px" }}
          >
            <ListItem>
              <ListItemText
                primary={hotel.name}
                secondary={`Rating: ${hotel.rating} - Price Range: ${hotel.priceRange}`}
              />
            </ListItem>
          </Paper>
        ))}
      </List>
    </div>
  );
};

export default NearbyHotelsPage;
