import React, { useState, useEffect } from "react";
import {
  Typography,
  TextField,
  Button,
  List,
  ListItem,
  ListItemText,
  Paper,
} from "@material-ui/core";

const NearbyHotelsPage = () => {
  const [location, setLocation] = useState("");
  const [hotels, setHotels] = useState([]);

  // Dummy data for demonstration
  const dummyHotels = [
    { name: "Hotel A", rating: 4.5, priceRange: "$$$" },
    { name: "Hotel B", rating: 4.2, priceRange: "$$" },
    { name: "Hotel C", rating: 4.8, priceRange: "$$$$" },
    { name: "Hotel D", rating: 3.9, priceRange: "$" },
    { name: "Hotel E", rating: 4.7, priceRange: "$$$$" },
  ];

  // Function to fetch nearby hotels (replace with your API call)
  const fetchNearbyHotels = async () => {
    // Simulating API call delay
    setTimeout(() => {
      setHotels(dummyHotels);
    });
  };

  useEffect(() => {
    // Fetch nearby hotels when the component mounts (commented for dummy data)
    // fetchNearbyHotels();
  }, [location]);

  const handleLocationChange = (event) => {
    setLocation(event.target.value);
  };

  const handleSearch = () => {
    // Trigger a new hotel search when the user clicks the search button
    fetchNearbyHotels();
  };

  return (
    <div>
      <Typography variant="h4" gutterBottom>
        Nearby Hotels
      </Typography>
      <TextField
        label="Enter Location"
        variant="outlined"
        fullWidth
        value={location}
        onChange={handleLocationChange}
      />
      <Button variant="contained" color="primary" onClick={handleSearch}>
        Search
      </Button>

      <List>
        {hotels.map((hotel, index) => (
          <Paper
            key={index}
            elevation={3}
            style={{ margin: "16px", padding: "16px" }}
          >
            <ListItem>
              <ListItemText
                primary={hotel.name}
                secondary={`Rating: ${hotel.rating} - Price Range: ${hotel.priceRange}`}
              />
            </ListItem>
          </Paper>
        ))}
      </List>
    </div>
  );
};

export default NearbyHotelsPage;
